mapper.select:
SELECT * FROM SV_SURVEYS 
WHERE writer LIKE #{username,jdbcType=VARCHAR}
ORDER BY ID DESC

mapper.update:
UPDATE sv_surveys SET
title = #{title,jdbcType=VARCHAR}, msg_subject = #{msgSubject,jdbcType=VARCHAR}, msg = #{msg,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, start_date = #{startDate,jdbcType=VARCHAR}, end_date = #{endDate,jdbcType=VARCHAR}, target_category1 = #{targetCategory1,jdbcType=VARCHAR}, target_category2 = #{targetCategory2,jdbcType=VARCHAR}, target_branches = #{targetBranches,jdbcType=VARCHAR}, limit = #{limit,jdbcType=VARCHAR}, writer = #{writer,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, part = #{part,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=NUMERIC}
