mapper.select:
    SELECT
		id, username, name, password, part, tel, email
    FROM BOOK
    WHERE username = #{username,jdbcType=VARCHAR}
    	AND
    	password = #{password,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO user (id, username, name, password, part, tel, email)
VALUES (#{id,jdbcType=NUMERIC}, #{username,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{part,jdbcType=VARCHAR}, #{tel,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}
)

mapper.update:
UPDATE user 
SET username = #{username,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  part = #{part,jdbcType=VARCHAR}
  tel = #{tel,jdbcType=VARCHAR}
  email = #{email,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=NUMERIC}
