mapper.select:
    SELECT
		*
    FROM sv_users
    WHERE username = #{username,jdbcType=VARCHAR}
    	AND
    	password = #{password,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO user (id, username, name, password, part, tel, email, user_level)
VALUES (#{id,jdbcType=NUMERIC}, #{username,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{part,jdbcType=VARCHAR}, #{tel,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, , #{userLevel,jdbcType=VARCHAR}
)
