mapper.select:
select distinct(etc_01) from tbcr_1040
where ptcp_tit like #{category,jdbcType=VARCHAR}
ORDER BY etc_01 ASC

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp from tbcr_1000 c, tbcr_1040
	where
		c.cst_no = tbcr_1040.cst_no
		and tbcr_1040.ptcp_tit like #{category,jdbcType=VARCHAR}
		and tbcr_1040.etc_01 like #{branch,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}
