mapper.select:
select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, e.cat_nm, d.ptcp_dttm, d.etc_04 from tbcr_1000 c, TBCR_1040 d
	, tbcr_1041 e
	where
		e.CAT_CD=d.CAT_CD
		and c.cst_no = d.cst_no
		and e.ptcp_tit like #{category,jdbcType=VARCHAR}
		and d.etc_04 like #{branch,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, e.cat_nm, d.ptcp_dttm, d.etc_04 from tbcr_1000 c, TBCR_1040 d
	, tbcr_1041 e
	where
		d.CAT_CD=e.CAT_CD
		and c.cst_no = d.cst_no
		and e.ptcp_tit like #{category,jdbcType=VARCHAR}
		and d.etc_04 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}

select count(*) from (
	select distinct c.cst_no 
	from tbcr_1000 c, TBCR_1040 d, TBCR_1041 e
	where
		d.cat_cd=e.cat_cd
		and c.cst_no = d.cst_no
		and e.cat_nm like #{category,jdbcType=VARCHAR}
		and d.etc_04 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}
