mapper.select:
select distinct(etc_04) from TBCR_1040
where cat_cd in (select cat_cd from TBCR_1041 where
cat_nm like #{category,jdbcType=VARCHAR}
cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000', 'BAE000000000','BAF000000000','BAG000000000','BAH000000000', 'BAI000000000','BAJ000000000')
)
ORDER BY etc_04 ASC

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, e.cat_nm, d.ptcp_dttm, d.etc_04 from tbcr_1000 c, TBCR_1040 d
	, tbcr_1041 e
	where
		e.CAT_CD=d.CAT_CD
		and c.cst_no = d.cst_no

			and e.cat_nm like #{category,jdbcType=VARCHAR}


			and e.cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')


		and d.etc_04 like #{branch,jdbcType=VARCHAR}

) 

where rownum <= #{limit,jdbcType=NUMERIC}

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, e.cat_nm, d.ptcp_dttm, d.etc_04 from tbcr_1000 c, TBCR_1040 d
	, tbcr_1041 e
	where
		d.CAT_CD=e.CAT_CD
		and c.cst_no = d.cst_no

			and e.cat_nm like #{category,jdbcType=VARCHAR}


			and e.cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')

		and d.etc_04 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}

) 

where rownum <= #{limit,jdbcType=NUMERIC}

select count(*) from (
	select distinct c.cst_no 
	from tbcr_1000 c, TBCR_1040 d, TBCR_1041 e
	where
		d.cat_cd=e.cat_cd
		and c.cst_no = d.cst_no

			and e.cat_nm like #{category,jdbcType=VARCHAR}


			and e.cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')

		and d.etc_04 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}
) 

where rownum <= #{limit,jdbcType=NUMERIC}
