mapper.select:
select distinct(etc_01) from TBCR_1040
where ptcp_tit like #{category,jdbcType=VARCHAR}
ORDER BY etc_01 ASC

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, d.ptcp_tit, d.ptcp_dttm, d.etc_01 from tbcr_1000 c, TBCR_1040 d
	where
		c.cst_no = d.cst_no
		and d.ptcp_tit like #{category,jdbcType=VARCHAR}
		and d.etc_01 like #{branch,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}

select * from (
	select distinct c.cst_no, c.cst_nm, c.hp, d.ptcp_tit, d.ptcp_dttm, d.etc_01 from tbcr_1000 c, TBCR_1040 d
	where
		c.cst_no = d.cst_no
		and d.ptcp_tit like #{category,jdbcType=VARCHAR}
		and d.etc_01 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}

select count(*) from (
	select distinct c.cst_no 
	from tbcr_1000 c, TBCR_1040 d
	where
		c.cst_no = d.cst_no
		and d.ptcp_tit like #{category,jdbcType=VARCHAR}
		and d.etc_01 like #{branch,jdbcType=VARCHAR}
		and d.ptcp_dttm >= #{startDate,jdbcType=VARCHAR}
		and d.ptcp_dttm <= #{endDate,jdbcType=VARCHAR}
	order by DBMS_RANDOM.RANDOM
) where rownum <= #{limit,jdbcType=NUMERIC}
