mapper.select:
select distinct(dep_nm) from VW_SURVEY
where cat_cd in (select cat_cd from VW_SURVEY where
cat_nm like #{category,jdbcType=VARCHAR}
cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000', 'BAE000000000','BAF000000000','BAG000000000','BAH000000000', 'BAI000000000','BAJ000000000')
)
ORDER BY dep_nm ASC

select * from (
	select distinct cst_no, cst_nm, hp, cat_nm, cont_dd, dep_nm from VW_SURVEY
	where 1


			and cat_nm like #{category,jdbcType=VARCHAR}


			cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')


		and dep_nm like #{branch,jdbcType=VARCHAR}

) 

where rownum <= #{limit,jdbcType=NUMERIC}

select * from (
	select distinct cst_no, cst_nm, hp, cat_nm, cont_dd, dep_nm from VW_SURVEY
	where 1

			and cat_nm like #{category,jdbcType=VARCHAR}


			and cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')

		and dep_nm like #{branch,jdbcType=VARCHAR}
		and cont_dd >= #{startDate,jdbcType=VARCHAR}
		and cont_dd <= #{endDate,jdbcType=VARCHAR}

) 

where rownum <= #{limit,jdbcType=NUMERIC}

select count(*) from (
	select distinct cst_no 
	from VW_SURVEY
	where
		1

			and cat_nm like #{category,jdbcType=VARCHAR}


			and cat_cd in ('BAA000000000','BAB000000000','BAC000000000','BAD000000000',
			'BAE000000000','BAF000000000','BAG000000000','BAH000000000',
			'BAI000000000','BAJ000000000')

		and dep_nm like #{branch,jdbcType=VARCHAR}
		and cont_dd >= #{startDate,jdbcType=VARCHAR}
		and cont_dd <= #{endDate,jdbcType=VARCHAR}
) 

where rownum <= #{limit,jdbcType=NUMERIC}
